CREATE DATABASE IF NOT EXISTS super_week;

/*------------------user table----------------*/
CREATE TABLE user 
(
    id int PRIMARY KEY NOT NULL AUTO_INCREMENT,
    email varchar(255),
    first_name varchar(255),
    last_name varchar(255),
    password varchar(255)
);


